#include <iostream>
#include <string>
#include <vector>

using namespace std;

bool isStretchy(string s, string w)
{
    if(s.length() < w.length())
        return false;
    int i = 0,j=0;
    int back_count = 0;

    while(i<s.length())
    {
        if(j<w.length() && s[i] == w[j])
        {
            if(i > 0 && j > 0 && s[i] == s[i-1] && s[i-1] == w[j-1])
                back_count++;
            else
                back_count = 1;
            i++;
            j++;
        }
        else
        {
            if((i == 0 && j == 0) || s[i] != s[i-1])
                return false;
            int c  = 1;
            i++;
            while(i<s.length() && s[i] == s[i-1])
            {
                c++;
                i++;
            }
            if(c + back_count < 3)
                return false;
                
        }
    }
    if(j == w.length())
        return true;
    else
        return false;
}

int expressiveWords(string S, vector<string>& words)
{
    int res  = 0;
    for(string word : words)
    {
        if(isStretchy(S, word))
            res++;
    }
    return res;
}

